import "@openzeppelin/contracts/access/Ownable.sol";
import "./ICreditOracle.sol";

/// @notice A simple oracle for testing/demo.
/// Owner can whitelist borrowers with caps, apr and minimum duration.
contract CreditOracle is ICreditOracle, Ownable {
    struct Terms { bool allowed; uint128 maxAmount; uint32 aprBps; uint64 minDuration; }
    mapping(address => Terms) public termsOf;

    constructor() Ownable(msg.sender) {}

    function setTerms(address borrower, bool allowed, uint128 maxAmount, uint32 aprBps, uint64 minDuration)
        external
        onlyOwner
    {
        termsOf[borrower] = Terms({allowed: allowed, maxAmount: maxAmount, aprBps: aprBps, minDuration: minDuration});
    }

    function getTerms(address borrower, uint256 requestedAmount)
        external
        view
        override
        returns (bool allowed, uint256 maxAmount, uint256 aprBps, uint256 minDuration)
    {
        Terms memory t = termsOf[borrower];
        allowed = t.allowed && requestedAmount <= t.maxAmount;
        maxAmount = t.maxAmount;
        aprBps = t.aprBps;
        minDuration = t.minDuration;
    }
}","keccak256":"0x8e41d61357e15c0182fe7a6a2be49f367e4bd42e740c70c6f81e8324bd08325a","license":"MIT"},"contracts/ICreditOracle.sol":{"content":"//SPDX-License-Identifier: MIT
