pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./ICreditOracle.sol";

/// @notice Simple bank that accepts XRP and issues uncollateralized loans based on an oracle.
contract XRPBank is Ownable, ReentrancyGuard {
    uint256 private constant YEAR = 365 days;

    struct Loan {
        uint256 principal;   // wei
        uint256 aprBps;      // annual percentage rate basis points / cost of the loan
        uint64  start;       // timestamp
        uint64  due;         // timestamp
        bool    active;
    }

    ICreditOracle public oracle;

    // Depositor bookkeeping (no interest to depositors in this simple demo)
    mapping(address => uint256) public depositOf;
    uint256 public totalDeposits;

    // One active loan per borrower
    mapping(address => Loan) public loanOf;
    uint256 public totalActivePrincipal;

    event OracleUpdated(address indexed oracle);
    event Deposited(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event LoanIssued(address indexed borrower, uint256 amount, uint256 aprBps, uint256 due);
    event Repaid(address indexed borrower, uint256 principal, uint256 interest, uint256 paid);

    constructor(address oracleAddress) Ownable(msg.sender) {
        oracle = ICreditOracle(oracleAddress);
        emit OracleUpdated(oracleAddress);
    }

    receive() external payable {
        // Allow plain transfers to count as deposits
        _deposit(msg.sender, msg.value);
    }

    function setOracle(ICreditOracle _oracle) external onlyOwner {
        oracle = _oracle;
        emit OracleUpdated(address(_oracle));
    }

    /// @notice Explicit deposit
    function deposit() external payable nonReentrant {
        require(msg.value > 0, "NO_VALUE");
        _deposit(msg.sender, msg.value);
    }

    function _deposit(address user, uint256 amount) internal {
        depositOf[user] += amount;
        totalDeposits   += amount;
        emit Deposited(user, amount);
    }

    /// @notice Withdraw depositors available funds (limited by contract liquidity)
    function withdraw(uint256 amount) external nonReentrant {
        require(amount > 0, "NO_AMOUNT");
        uint256 bal = depositOf[msg.sender];
        require(bal >= amount, "INSUFFICIENT_DEPOSIT");
        // Liquidity check: only on-chain balance can be withdrawn (loans reduce liquidity)
        require(address(this).balance >= amount, "INSUFFICIENT_LIQUIDITY");

        // Effects
        depositOf[msg.sender] = bal - amount;
        totalDeposits        -= amount;

        // Interaction
        (bool ok, ) = msg.sender.call{value: amount}("");
        require(ok, "WITHDRAW_FAIL");

        emit Withdrawn(msg.sender, amount);
    }

    /// @notice Request a loan; terms come from oracle. One active loan per borrower.
    /// @param amount Amount of XRP (wei) requested
    /// @param minDueAt Borrower’s minimum acceptable due timestamp (protects against unexpectedly short durations)
    function requestLoan(uint256 amount, uint64 minDueAt) external nonReentrant {
        require(amount > 0, "NO_AMOUNT");
        Loan storage L = loanOf[msg.sender];
        require(!L.active, "LOAN_ACTIVE");

        (bool allowed, uint256 maxAmount, uint256 aprBps, uint256 minDuration) =
            oracle.getTerms(msg.sender, amount);

        require(allowed, "BORROWER_NOT_ALLOWED");
        require(amount <= maxAmount, "ABOVE_MAX");
        require(aprBps > 0, "APR_ZERO");
        require(minDuration > 0, "DUR_ZERO");

        uint64 start = uint64(block.timestamp);
        uint64 due   = start + uint64(minDuration);
        require(due >= minDueAt, "DUE_TOO_SOON");

        // Liquidity: bank must have enough native balance to fund loan
        require(address(this).balance >= amount, "BANK_ILLQ");

        // Effects before interaction (plus nonReentrant)
        L.principal = amount;
        L.aprBps    = aprBps;
        L.start     = start;
        L.due       = due;
        L.active    = true;
        totalActivePrincipal += amount;

        // Payout
        (bool ok, ) = msg.sender.call{value: amount}("");
        require(ok, "PAYOUT_FAIL");

        emit LoanIssued(msg.sender, amount, aprBps, due);
    }

    /// @notice View function to compute current interest & total owed.
    function quoteOwed(address borrower) public view returns (uint256 principal, uint256 interest, uint256 total) {
        Loan memory L = loanOf[borrower];
        if (!L.active) return (0, 0, 0);

        principal = L.principal;
        uint256 elapsed = block.timestamp > L.start ? (block.timestamp - L.start) : 0;

        // Simple interest = principal * apr * time / (bpsDenom * YEAR)
        // 1 BPS = 0.01% = 0.0001 = 1/10000
        // 365 days = time in seconds for a year
        interest = (principal * L.aprBps * elapsed) / (10000 * YEAR);
        total = principal + interest;
    }

    /// @notice Repay the loan in full. Excess is refunded.
    function repay() external payable nonReentrant {
        Loan storage L = loanOf[msg.sender];
        require(L.active, "NO_LOAN");

        (uint256 principal, uint256 interest, uint256 total) = quoteOwed(msg.sender);
        require(msg.value >= total, "INSUFFICIENT_REPAY");

        // Effects
        L.active = false;
        L.principal = 0;
        L.aprBps = 0;
        L.start = 0;
        L.due = 0;
        totalActivePrincipal -= principal;

        emit Repaid(msg.sender, principal, interest, msg.value);

        // Refund any overpayment
        uint256 change = msg.value - total;
        if (change > 0) {
            (bool ok, ) = msg.sender.call{value: change}("");
            require(ok, "REFUND_FAIL");
        }
    }

    // ----- Admin safety valves (optional) -----

    /// @notice Sweep accidental ERC20s or tokens sent to this contract (not XRP).
    function rescueERC20(address token, address to, uint256 amount) external onlyOwner {
        (bool ok, bytes memory data) =
            token.call(abi.encodeWithSignature("transfer(address,uint256)", to, amount));
        require(ok && (data.length == 0 || abi.decode(data, (bool))), "ERC20_RESCUE_FAIL");
    }
}


